import './App.css';
import {Component} from 'react';

class App extends Component{
constructor(){
  super();
  this.state={
    monsters:[ ],
    searchFeild:'',


  };
}

componentDidMount(){
  fetch('https://jsonplaceholder.typicode.com/users')
  .then((response)=>response.json())
  .then((users)=>this.setState(()=>{
    return {monsters:users};

  },()=>{console.log(this.state)}
  ))
}


OnsearcChcahnge= (event)=>{
  const searchFeild =event.target.value.toLocaleLowerCase();
  this.setState(()=>{
    return{searchFeild};
  })}

render(){
  const{monsters}=this.state

  const filteredMonsters=this.state.monsters.filter((monster)=>{
    return monster.name.toLocaleLowerCase().includes(this.state.searchFeild)//When you go to modifiy array create new one(immutability) 
  })


  return(
    <div className='App'>
    <div>
    <input placeholder='seach' onChange={this.OnsearcChcahnge}></input>
    </div>  
    {filteredMonsters.map((monsters)=>{
      return <h1>{monsters.name}</h1>
    })}
    </div>



  );
  }
}

export default App
