import './App.css';
import {Component} from 'react';

class App extends Component{
constructor(){
  super();
  this.state={
    monsters:[ ],
    searchFeild:'',


  };
}

componentDidMount(){
  fetch('https://jsonplaceholder.typicode.com/users')
  .then((response)=>response.json())
  .then((users)=>this.setState(()=>{
    return {monsters:users};

  },()=>{console.log(this.state)}
  ))
}

render(){
  const filteredmonsters= this.state.monsters.filter(monsters =>{
return monsters.name.toLcaleLowerCase().includes(this.state.searchFeild.toLowerCase())
  })

  return(
    <div className='App'>
    <div>
    <input placeholder='seach 'onChange={(event)=>
      { this.setState({searchFeild:event.target.value})}}></input>
    </div>

    {filteredmonsters.map((monsters)=>{
      return <h1>{monsters.name}</h1>
    })}
    </div>



  );
  }
}

export default App
